const selectSingle=document.querySelector(".form-select"),selectSingle_title=selectSingle.querySelector(".form-select__title"),selectSingle_labels=selectSingle.querySelectorAll(".form-select__label");selectSingle_title.addEventListener("click",(()=>{"active"==selectSingle.getAttribute("data-state")?selectSingle.setAttribute("data-state",""):selectSingle.setAttribute("data-state","active")}));for(let e=0;e<selectSingle_labels.length;e++)selectSingle_labels[e].addEventListener("click",(e=>{selectSingle_title.textContent=e.target.textContent,selectSingle.setAttribute("data-state","")}));